mapper.update:
UPDATE
    member_login
SET login_date_time = #{loginDateTime},
    login_flg       = #{loginFlg}
WHERE customer_no = #{membershipNumber}

UPDATE
    member_login
SET login_flg = #{loginFlg}
WHERE customer_no = #{membershipNumber}

UPDATE member_login
SET last_password = password,
    password      = #{password}
WHERE customer_no = #{membershipNumber}
